import { useState } from 'react';
import {evaluate} from 'mathjs';

function App() {
  let [result, setResult] = useState("");
  let handleClick = (event) => {
    setResult(result + event.target.innerHTML);
  }

  let handleChange = (event) => {
    setResult(event.target.value);
  }

  let handleEqualClick = () => {
    result = evaluate(result);
  }

  let handleClearClick = () => {
    setResult("");
  }

  let handleCClick = () => {
    setResult(result.substring(0,result.length-1))
  }
  return (
    <>
      <div className="container">
        <form>
          <input type="text" onChange={handleChange} value={result}/>
        </form>
        <div className="keypad">
          <button onClick={handleClearClick} id='clear'>Clear</button>
          <button onClick={handleCClick} >C</button>
          <button onClick={handleClick} >&divide;</button>
          <button onClick={handleClick} >7</button>
          <button onClick={handleClick} >8</button>
          <button onClick={handleClick} >9</button>
          <button onClick={handleClick} >&times;</button>
          <button onClick={handleClick} >4</button>
          <button onClick={handleClick} >5</button>
          <button onClick={handleClick} >6</button>
          <button onClick={handleClick} >&minus;</button>
          <button onClick={handleClick} >1</button>
          <button onClick={handleClick} >2</button>
          <button onClick={handleClick} >3</button>
          <button onClick={handleClick}  id='plus'>&#43;</button>
          <button onClick={handleClick} >0</button>
          <button onClick={handleClick} >.</button>
          <button onClick={handleEqualClick} >=</button>
        </div>
      </div>
    </>
  );
}

export default App;
